      //Mantenme los datos que tengo en user y agregame lo que yo te entrego aqui (usuario)
      ...user,
      usuario,
    ];
    //Luego actualizamos (o setteamos) el state
    setUser(addUsuario);
  };


  return(

      <main>
      {/* <FondoLogin/> */}
